import React, {useEffect, useState} from 'react';
import './App.css';
import Recipe from './Recipe'

const App = () => {

  const APP_ID = "732d490f"; 
  const appKey = "ea1d0f7d3b451a26e286a2715f9d4a23"; 

  const [recipes, setRecipes] = useState([]); 
  const [search, setSearch] = useState('');
  const [query, setQuery] = useState('chicken'); 
  useEffect(() => {
    getRecipe();

  }, [query]);

  const getRecipe = async () => {
    const response = await fetch(`https://api.edamam.com/search?q=${query}&app_id=${APP_ID}&app_key=${appKey}`); 
    const data = await response.json(); 
    setRecipes(data.hits); 
  }

  const updateSearch = e => {
    setSearch(e.target.value)
  }
  const getSearch = e =>{
    e.preventDefault(); 
    setQuery(search)
  }
  return (
    <div className='App'>
      <form className='search-form'>
        <input className='search-bar' value= {search} onChange={updateSearch} type='text'/>
        <button className='search-button' onClick={getSearch} type='submit'>Search</button>
      </form>
      <div className="container-fluid">
        <div className="row">
          {recipes.map(recipe => (

            <Recipe key={recipe.recipe.label}
              title = {recipe.recipe.label} 
              calories = {recipe.recipe.calories}
              img = {recipe.recipe.image}
            />

          ))}
       </div>
      </div>

    </div>
  )
}

export default App;
