import React, {useEffect, useState} from 'react';
import './App.css';

const App = () => {

  const [repo, setRepo] = useState([]); 
  const [search, setSearch] = useState('');
  const [query, setQuery] = useState('tetris'); 
  useEffect(() => {
    sendSearch();
    console.log(repo);

  }, [query]);

  const sendSearch = async () => {
    //curl https://api.github.com/search/repositories?q=tetris+language:assembly&sort=stars&order=desc

    const response = await fetch(`https://api.github.com/search/repositories?q=${query}+&sort=stars&order=desc`); 
    const data = await response.json(); 
    setRepo(data.items); 
  }

  const updateSearch = e => {
    setSearch(e.target.value)
  }
  const getSearch = e =>{
    e.preventDefault(); 
    setQuery(search)
  }
  return (
    <div className='App'>
      <form className='search-form'>
        <input className='search-bar' value= {search} onChange={updateSearch} type='text'/>
        <button className='search-button' onClick={getSearch} type='submit'><span> </span>Search</button>
      </form>
      <div className="container-fluid">
        <div className="row">
          <div className="col-sm-12 col-md-6">
          <table className="table">
            <thead>
              <tr>
                <th scope="col">#</th>
                <th scope="col">Repo</th>
                <th scope="col">Rating</th>
                <th scope="col">folks</th>
              </tr>
            </thead>
            <tbody>
            {
              repo.map( (item, i) =>
              <tr key={item.id}>
                <th scope="row">{i}</th>
                <td> 
                  <p key={i}>{item.name}</p>
                </td>
                <td>Otto</td>
                <td> <p>{item.forks_count}</p></td>
              </tr>
              )
            }
            </tbody>
          </table>

          </div>

       </div>
      </div>

    </div>
  )
}

export default App;
